import React from 'react';

const Card = (props) => {
    const { name, gender, weight } = props;
    return (
        <div className='tc bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5'>
            <img src={`https://robohash.org/${props.id}?200x200`} alt='robots'/>
            <div>
                <h2> {props.name} </h2>
                <p> {props.gender} </p>
                <p> {props.weight} </p>
            </div>
        </div>
    );
}

export default Card
